// Get all input items
const items = $input.all();

// Filter out items that have no keys (empty objects)
const filtered = items.filter(item => Object.keys(item.json).length > 0);

if (filtered.length > 0) {
  // Preserve pairing with their original items
  return filtered.map((item, index) => ({
    json: item.json,
    pairedItem: { item: index },
  }));
} else {
  // Add placeholder item while keeping at least one paired reference
  return [
    {
      json: {
        first_name: "",
        last_name: "",
        __placeholder_empty: true
      },
      pairedItem: { item: 0 } // link to the first input item so downstream knows the context
    }
  ];
}

